import { Component } from '@angular/core';
import { MarcaService } from '../../marca.service';
import { Marca } from '../../models/marca.model';
@Component({
  selector: 'gc-marca-create',
  templateUrl: './marca-create.component.html',
})
export class MarcaCreateComponent {
  sigla!: string;
  descricao!: string;

  constructor(private marcaService: MarcaService) {}
  save() {
    const marca: Marca = { sigla: this.sigla, descricao: this.descricao };
    this.marcaService.save(marca).subscribe(
      (res) => {
        console.log(res);
      },
      (error) => console.log(error)
    );
  }
}
<form (ngSubmit)="save()">
  <h2>Cadastro de curso</h2>
  <div>
    <label for="nome">Nome</label>
    <input id="nome" name="nome" [(ngModel)]="nome" type="text" />
  </div>
  <div>
    <label for="cargaHoraria">Carga horária</label>
    <input
      id="cargaHoraria"
      name="cargaHoraria"
      type="number"
      [(ngModel)]="cargaHoraria"
    />
  </div>
  <div>

  </div>
  <button>Salvar</button>
</form>
