import { Marca } from './../../models/marca.model';
import { MarcaService } from './../../marca.service';
import { Component, OnInit } from '@angular/core';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';
@Component({
  selector: 'gc-marca-create',
  templateUrl: './marca-create.component.html',
})
export class MarcaCreateComponent implements OnInit {
  marcaForm!: FormGroup;
  submitted = false;
  descricao: any;
  sigla: any;

  constructor(
    private marcaService: MarcaService,
    private formBuilder: FormBuilder
  ) {}
  ngOnInit(): void {
    this.marcaForm = this.formBuilder.group({
      sigla: new FormControl('', [Validators.required]),
      descricao: new FormControl('', [Validators.required]),
    });
  }
  save() {
    if (this.marcaForm.valid) {
      const marca = this.marcaForm.getRawValue() as Marca;
      this.marcaService.save(marca).subscribe(
        () => (this.submitted = true),
        (error) => console.log(error)
      );
    }
  }
  addMarcaForm() {
    this.submitted = false;
    this.marcaForm.reset();
  }
}
<form (ngSubmit)="save()">
  <h2>Cadastro de curso</h2>
  <div>
    <label for="sigla">Nome</label>
    <input id="sigla" name="sigla" [(ngModel)]="sigla" type="text" />
  </div>
  <div>
    <label for="cargaHoraria">Carga horária</label>
    <input
      id="cargaHoraria"
      name="cargaHoraria"
      type="number"
      [(ngModel)]="cargaHoraria"
    />
  </div>
  <div>
    <select
      [ngModel]="modalidade"
      (ngModelChange)="onChange($event)"
      name="modalidade"
    >
      <option [value]="m.modalidade" *ngFor="let m of modalidades">
        {{ m.descricao }}
      </option>
    </select>
  </div>
  <button>Salvar</button>
</form>
