import { Marca } from './../../models/marca.model';
import { MarcaService } from './../../marca.service';
import { Component, OnInit } from '@angular/core';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';
@Component({
  selector: 'gc-marca-create',
  templateUrl: './marca-create.component.html',
})
export class MarcaCreateComponent implements OnInit {
  marcaForm!: FormGroup;
  submitted = false;

  constructor(
    private marcaService: MarcaService,
    private formBuilder: FormBuilder
  ) {}
  ngOnInit(): void {
    this.marcaForm = this.formBuilder.group({
      nome: new FormControl('', [Validators.required, Validators.minLength(5)]),
      cargaHoraria: new FormControl('', [Validators.required]),
      modalidade: new FormControl('', [Validators.required]),
    });
  }
  save() {
    if (this.marcaForm.valid) {
      const marca = this.marcaForm.getRawValue() as Marca;
      this.marcaService.save(marca).subscribe(
        () => (this.submitted = true),
        (error) => console.log(error)
      );
    }
  }
  addMarcaForm() {
    this.submitted = false;
    this.marcaForm.reset();
  }
}
<form (ngSubmit)="save()">
  <h2>Cadastro de marca</h2>
  <div>
    <label for="sigla">Sigla</label>
    <input id="sigla" name="sigla" [(ngModel)]="sigla" type="text" />
  </div>
  <div>
    <label for="descricao">Descricao</label>
    <input
      id="descricao"
      name="descricao"
      [(ngModel)]="descricao"
      type="text"
    />
  </div>
  <button>Salvar</button>
</form>
