import { Curso } from './../../models/marca.model';
import { Modalidade } from './../../models/modalidade.model';
import { CursoService } from './../../marca.service';
import { Component, OnInit } from '@angular/core';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';
@Component({
  selector: 'gc-marca-create',
  templateUrl: './marca-create.component.html',
})
export class CursoCreateComponent implements OnInit {
  marcaForm!: FormGroup;
  submitted = false;
  modalidades = [
    { modalidade: Modalidade.PRESENCIAL, descricao: 'Presencial' },
    { modalidade: Modalidade.EAD, descricao: 'Ensino a Distância' },
    { modalidade: Modalidade.HIBRIDO, descricao: 'Híbrido' },
  ];
  constructor(
    private marcaService: CursoService,
    private formBuilder: FormBuilder
  ) {}
  ngOnInit(): void {
    this.marcaForm = this.formBuilder.group({
      nome: new FormControl('', [Validators.required, Validators.minLength(5)]),
      cargaHoraria: new FormControl('', [Validators.required]),
      modalidade: new FormControl('', [Validators.required]),
    });
  }
  save() {
    if (this.marcaForm.valid) {
      const marca = this.marcaForm.getRawValue() as Curso;
      this.marcaService.save(marca).subscribe(
        () => (this.submitted = true),
        (error) => console.log(error)
      );
    }
  }
  addCursoForm() {
    this.submitted = false;
    this.marcaForm.reset();
  }
}
