import { Component } from '@angular/core';
import { MarcaService } from '../veiculo.service';
import { Marca } from '../models/veiculo.model';
import { Router } from '@angular/router';
import { VeiculoService } from '../veiculo.service';
@Component({
  selector: 'gc-veiculo-list',
  templateUrl: './veiculo-list.component.html',
  styleUrls: ['./veiculo-list.component.css'],
})
export class MarcaListComponent {
  constructor(private veiculoService: VeiculoService, private router: Router) {}
  veiculos: Marca[] = [];
  ngOnInit() {
    this.veiculoService.listarMarcas().subscribe((dados) => {
      this.veiculos = dados;
    });
  }
  deleteMessage = false;
  delete(id?: number) {
    this.veiculoService.delete(id!).subscribe(
      (dado) => {
        console.log(dado);
        this.deleteMessage = true;
        this.veiculoService.listarMarcas().subscribe((dados) => {
          this.veiculos = dados;
        });
      },
      (error) => console.log(error)
    );
  }
  updateMarca(id?: number) {
    this.router.navigate(['edit-curso', id]);
  }
}
