import { Component, OnInit } from '@angular/core';
import {
FormBuilder,
FormControl,
FormGroup,
Validators,} from '@angular/forms';
import { MarcaService } from 'src/app/marca/marca.service';
import { Marca } from 'src/app/marca/models/marca.model';
import { VeiculoService } from '../veiculo.service';
import { Veiculo } from '../veiculo.model';
@Component({
selector: 'gc-veiculo-create',
templateUrl: './veiculo-create.component.html',
styleUrls: ['./veiculo-create.component.css'],
})
export class VeiculoCreateComponent implements OnInit {
veiculoForm!: FormGroup;
submitted = false;
cursos: Marca[] = [];
constructor(
private aulaService: VeiculoService,
private formBuilder: FormBuilder,
private cursoService: MarcaService
) {
this.cursoService.listarMarcas().subscribe((dados: Marca[]) => {
this.cursos = dados;
});
}
ngOnInit(): void {
this.aulaForm = this.formBuilder.group({
descricao: new FormControl('', [
Validators.required,
Validators.minLength(5),
]),
tempoDuracao: new FormControl('', [Validators.required]),
curso: new FormControl('', [Validators.required]),
});
}
save() {
if (this.aulaForm.valid) {
const aula = this.aulaForm.getRawValue() as Veiculo;
this.aulaService.save(aula).subscribe(
() => (this.submitted = true),
(error) => console.log(error)
);
}
}
addVeiculoForm() {
this.submitted = false;
this.aulaForm.reset();
}
}
